import React, { Component } from 'react';
import FormUserDetails from './FormUserDetails'
import FromPersonalDetails, { FormPersonalDetails } from './FormPersonalDetails'

 class UserForm extends Component {
     state = {
         step: 1,
         firstName: '',
         lastName:'',
         email:'',
         occupation:'',
         city:'',
         bio:''
     }

    // next step from
    nextStep = () =>{
        const { step }  = this.state;
        this.setState({
            step : step + 1
        })
    }
       // prev step from
       prevStep = () =>{
        const { step }  = this.state;
        this.setState({
            step : step - 1
        })
    }

    // handle fileds chnage 
    handleChange = input => e => {
        this.setState({[input]: e.target.value});
    }

    render() {

        const { step } = this.state;
        const { firstName, lastName, email , occupation, city, bio} = this.state;
        const value = { firstName, lastName, email , occupation, city, bio}
        switch(step){
            case 1:
            return(
                <FormUserDetails 
                 nextStep = {this.nextStep}
                 handleChange  = {this.handleChange}
                 values = {value}
                
                />
            )
            case 2 :
                return(
                <FormPersonalDetails 
                 nextStep = {this.nextStep}
                 handleChange  = {this.handleChange}
                 values = {value}
                
                />
                )
            case 3 :
                return <h1>confirm details</h1>
            case 4 :
                return <h1>Success details</h1>
        }
    }
}
export default UserForm;
